import React, { useState } from 'react';
import MapComponent from './MapComponent'; // Make sure this points to your actual Map component
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Spinner } from 'react-bootstrap';

function App() {
  const [countryInfo, setCountryInfo] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleMapClick = async (event) => {
    setLoading(true);
    const lat = event.latLng.lat();
    const lng = event.latLng.lng();
    const geocoder = new window.google.maps.Geocoder();

    geocoder.geocode({ location: { lat, lng } }, async (results, status) => {
      if (status === 'OK') {
        const addressComponents = results[0].address_components;
        const countryComponent = addressComponents.find(component =>
          component.types.includes('country')
        );
        if (countryComponent) {
          try {
            const response = await fetch(`https://restcountries.com/v3.1/name/${countryComponent.long_name}?fullText=true`);
            const data = await response.json();
            const countryData = data[0];
            setCountryInfo({
              country: countryComponent.long_name,
              flag: countryData.flags.svg,
              population: countryData.population,
              flagDescription: `The flag of ${countryData.name.common} has ${countryData.flags.altDescription}.`
            });
          } catch (error) {
            console.error('Failed to fetch country data', error);
          }
        } else {
          console.log('No country found at this location');
        }
      } else {
        console.error('Geocoder failed due to: ' + status);
      }
      setLoading(false);
    });
  };

  // ... (styling objects)
  const appStyle = {
    textAlign: 'center',
    backgroundColor: '#6B7FD7', // Nyanza for app background
    minHeight: '100vh',
    paddingTop: '5vh'
  };

  const titleStyle = {
    color: '#320E3B', // Russian violet for the title
    marginBottom: '1vh'
  };

  const mapHolderStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    paddingBottom: '3vh'
  };

  const cardStyle = {
    width: '60vw',
    backgroundColor: '#BCEDF6', // Non Photo blue for card background
    marginTop: '3vh',
    textAlign: 'center',
    boxShadow: '0 4px 8px rgba(0,0,0,0.25)',
    borderColor: '#DDFBD2' // Glaucous for card border
  };

  const cardTextStyle = {
    fontSize: '1.25em',
    color: '#4C2A85', // Tekhelet for text
  };

  const spinnerStyle = {
    color: '#DDFBD2', // Glaucous for spinner
    marginTop: '3vh'
  };


  return (
    <div className="App" style={appStyle}>
      <h1 style={titleStyle}>Country Information</h1>
      <p>Click anywhere on the map to see the country name, flag, and population.</p>
      <div className='mapHolder' style={mapHolderStyle}>
        <MapComponent onClick={handleMapClick} />
        {loading ? (
          <Spinner animation="border" role="status" style={spinnerStyle}>
            <span className="visually-hidden">Loading...</span>
          </Spinner>
        ) : (
          countryInfo && (
            <Card style={cardStyle}>
              <Card.Body>
                <Card.Title style={cardTextStyle}>{countryInfo.country}</Card.Title>
                <Card.Text style={cardTextStyle}>
                  <img src={countryInfo.flag} alt={countryInfo.flagDescription || `Flag of ${countryInfo.country}`} style={{ height: '100px' }} />
                  <br />
                  <span>Population:</span> {countryInfo.population.toLocaleString()}
                </Card.Text>
              </Card.Body>
            </Card>
          )
        )}
      </div>
    </div>
  );
}

export default App;
