import React, { useState } from 'react';
import MapComponent from './MapComponent';  // Assuming this is the name of your map file
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Spinner } from 'react-bootstrap';

function App() {
  const [countryInfo, setCountryInfo] = useState(null);
  const [loading, setLoading] = useState(false);  // New state to track loading

  const handleMapClick = async (event) => {
    setLoading(true);  // Start loading
    const lat = event.latLng.lat();
    const lng = event.latLng.lng();
    const geocoder = new window.google.maps.Geocoder();

    geocoder.geocode({ location: { lat, lng } }, async (results, status) => {
      if (status === 'OK') {
        const addressComponents = results[0].address_components;
        const countryComponent = addressComponents.find(component =>
          component.types.includes('country')
        );
        if (countryComponent) {
          const country = countryComponent.long_name;
          try {
            const flagResponse = await fetch(`https://restcountries.com/v3.1/name/${country}`);
            const flagData = await flagResponse.json();
            const flag = flagData[0].flags.svg;
            setCountryInfo({ country, flag });
            setLoading(false);  // Stop loading after fetching
          } catch (error) {
            console.error('Failed to fetch flag data', error);
            setLoading(false);  // Stop loading if there is an error
          }
        } else {
          console.log('No country found at this location');
          setLoading(false);  // Stop loading if no country component found
        }
      } else {
        console.error('Geocoder failed due to: ' + status);
        setLoading(false);  // Stop loading if geocoding fails
      }
    });
  };

  const appStyle = {
    textAlign: 'center',
    backgroundColor: '#6B7FD7', // Nyanza for app background
    minHeight: '100vh',
    paddingTop: '5vh'
  };

  const titleStyle = {
    color: '#320E3B', // Russian violet for the title
    marginBottom: '1vh'
  };

  const mapHolderStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    paddingBottom: '3vh'
  };

  const cardStyle = {
    width: '60vw',
    backgroundColor: '#BCEDF6', // Non Photo blue for card background
    marginTop: '3vh',
    textAlign: 'center',
    boxShadow: '0 4px 8px rgba(0,0,0,0.25)',
    borderColor: '#6B7FD7' // Glaucous for card border
  };

  const cardTextStyle = {
    fontSize: '1.25em',
    color: '#4C2A85', // Tekhelet for text
  };

  const spinnerStyle = {
    color: '#6B7FD7', // Glaucous for spinner
    marginTop: '3vh'
  };

  // ... (rest of your code where components are returned)

  return (
    <div className="App" style={appStyle}>
      <h1 style={titleStyle}>Country Information</h1>
      <p>Click anywhere on the map to see the country name and flag.</p>
      <div className='mapHolder' style={mapHolderStyle}>
        <MapComponent onClick={handleMapClick} />
        {loading ? (
          <Spinner animation="border" role="status" style={spinnerStyle}>
            <span className="visually-hidden">Loading...</span>
          </Spinner>
        ) : (
          countryInfo && (
            <Card style={cardStyle}>
              <Card.Body>
                <Card.Title style={cardTextStyle}>Country Information</Card.Title>
                <Card.Text style={cardTextStyle}>
                  <span>Country:</span> {countryInfo.country}<br />
                  <img src={countryInfo.flag} alt={`Flag of ${countryInfo.country}`} style={{ height: '100px' }} />
                </Card.Text>
              </Card.Body>
            </Card>
          )
        )}
      </div>
    </div>
  );
}

export default App;
