import React, { useState, useEffect } from 'react';
import { Spinner } from 'react-bootstrap';

function CountryFlag({ country }) {
  const [countryData, setCountryData] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (country) {
      setLoading(true); // Start loading
      fetch(`https://restcountries.com/v3.1/name/${country}?fullText=true`)
        .then(response => response.json())
        .then(data => {
          // Extract the required data and set it
          const flag = data[0].flags.svg;
          const population = data[0].population;
          const altText = `The flag of ${data[0].name.common} has ${data[0].flags.altDescription}.`;
          setCountryData({ flag, population, altText });
        })
        .catch(error => console.error('Error fetching country data:', error))
        .finally(() => setLoading(false)); // Stop loading
    }
  }, [country]);

  if (loading) {
    return (
      <Spinner animation="border" role="status" style={{ color: '#6B7FD7' }}>
        <span className="visually-hidden">Loading...</span>
      </Spinner>
    );
  }

  if (!countryData) {
    return null;
  }

  return (
    <>
      <img src={countryData.flag} alt={countryData.altText} className="img-fluid" style={{ height: '100px' }} />
      <p>Population: {countryData.population.toLocaleString()}</p>
    </>
  );
}

export default CountryFlag;
